public class jQuantumApplet extends javax.swing.JApplet {

    private String language = "en";

    private jQuantum frame;

    public void init() {
        if (getParameter("language") != null) language = getParameter("language");
        initComponents();
    }

    /** This method is called from within the init() method to
     * initialize the form.
     * WARNING: Do NOT modify this code. The content of this method is
     * always regenerated by the Form Editor.
     */
    private void initComponents() {
        startButton = new javax.swing.JButton();
        getContentPane().setLayout(new java.awt.FlowLayout());
        setBackground(new java.awt.Color(254, 255, 255));
        startButton.setText(" Start ");
        startButton.addActionListener(new java.awt.event.ActionListener() {

            public void actionPerformed(java.awt.event.ActionEvent evt) {
                startButtonActionPerformed(evt);
            }
        });
        getContentPane().add(startButton);
    }

    private void startButtonActionPerformed(java.awt.event.ActionEvent evt) {
        setCursor(new java.awt.Cursor(java.awt.Cursor.WAIT_CURSOR));
        if (frame != null) {
            frame.dispose();
        }
        frame = new jQuantum(language);
        setCursor(new java.awt.Cursor(java.awt.Cursor.DEFAULT_CURSOR));
        frame.setVisible(true);
    }

    private javax.swing.JButton startButton;
}
